mapper.update:
UPDATE CN_NOTE
cn_notebook_id=#{notebookId}, cn_user_id=#{userId}, cn_note_status_id=#{statusId}, cn_note_type_id=#{typeId}, cn_note_title=#{title}, cn_note_body=#{body}, cn_note_create_time=#{createTime}, cn_note_last_modify_time=floor(timestampdiff(microsecond,'1970-01-01 00:00:00',now(3))/1000)
WHERE cn_note_id=#{id}
